mapper.update:
update SYS_USER set 
LAST_LOGIN = #{LAST_LOGIN} 
where ID = #{ID}

mapper.select:
select u.ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.PHONE, u.POSITION, u.ADDR, u.WEIXIN, u.IDNO, u.JOBNO, u.AGE, case u.IS_MANAGER when 0 then '非项目经理' else '项目经理' end as IS_MANAGER, case u.SEX when 0 then '女' else '男' end as SEX, case u.STATUS when 0 then '禁用' else '启用' end as STATUS, r.ROLE_ID, r.ROLE_NAME from SYS_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID
and u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and u.ROLE_ID=#{pd.ROLE_ID}
and u.LAST_LOGIN >= #{pd.lastLoginStart}
and u.LAST_LOGIN <= #{pd.lastLoginEnd}
order by u.LAST_LOGIN desc

select count(*) counts from SYS_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and u.ROLE_ID=#{pd.ROLE_ID}
and u.LAST_LOGIN >= #{pd.lastLoginStart}
and u.LAST_LOGIN <= #{pd.lastLoginEnd}
order by u.LAST_LOGIN desc

select u.ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.PHONE, u.IS_MANAGER, r.ROLE_ID, r.ROLE_NAME from SYS_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') )
and u.ROLE_ID=#{ROLE_ID}
and u.LAST_LOGIN >= #{lastLoginStart}
and u.LAST_LOGIN <= #{lastLoginEnd}
order by u.LAST_LOGIN desc
